export const compareValues = (direction, a, b) => {
  if (a < b) {
    return -1 * direction;
  }
  if (a > b) {
    return direction;
  }
  return 0;
};
export class LocalSorter {
  static sort(data, field, direction, customCompare) {
    const dir = direction === 'asc' ? 1 : -1;
    const compare = customCompare ? customCompare : compareValues;
    // return data.sort((a, b) => compare.call(null, dir, a[field], b[field])); // FIXME fallback to dis in cas direct call fails
    return data.sort((a, b) => compare(null, dir, a[field], b[field]));
  }
}
